mutation {
  createTransaction(input: {amount: 100.50, payerID: "user-123", walletID: "wallet-456", currencyID: "curr-USD"}) {
    id
    amount
    rate
    description
    timestamp
    payer {
      id
      firstName
      lastName
    }
    wallet {
      id
      name
      currency {
        id
        abbreviation
        symbol
      }
      balance
    }
    location {
      id
      name
      lat
      lng
    }
  }
}

