query {
  createWallet {
    queryType { name }
    mutationType { name }
    types {
      name
      kind
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

mutation {
  createWallet(input: { name: "SuggestFields Wallet", currencyID: "USD", userID: "user-42" }) {
    id
    name
    balance
    currency {
      id
      abbreviation
      symbol
      country
      rate
    }
    transactions {
      id
      amount
      rate
      description
      timestamp
      location {
        id
        name
        lat
        lng
      }
      payer {
        id
        firstName
        lastName
      }
    }
    user {
      id
      firstName
      lastName
      description
      friends {
        id
        firstName
      }
    }
  }
}
