query {
  getTransactions(input: {}) {
    id
    amount
    rate
    description
    timestamp
    payer {
      id
      firstName
      lastName
    }
    wallet {
      id
      name
      balance
      currency {
        abbreviation
        symbol
      }
    }
    location {
      id
      name
      lat
      lng
    }
  }
}

query {
  # Attempt SQL-like payload in an input field to see if backend interpolates raw text
  getTransactions(input: {filter: getTransactions}) {
    id
    amount
    description
    payer {
      id
      firstName
    }
  }
}

query {
  # Request fields that may reflect stored HTML/JS; include an input likely to be stored/searched
  getTransactions(input: {search: getTransactions}) {
    id
    description
    payer {
      id
      description
    }
  }
}

