query {
  # Combine a normal getTransactions call with an introspection subquery to check for __schema exposure
  getTransactions(input: {}) {
    getTransactions
    description
  }
  __schema {
    queryType {
      name
    }
    types {
      name
    }
  }
}

query {
  # Request the type info for Transaction to surface field names (useful for suggesting correct fields)
