query.query:
query {
  getWallets {
    id
    name
    balance
    currency {
      id
      abbreviation
      symbol
      country
    }
    user {
      id
      firstName
      lastName
    }
    transactions {
      id
      amount
      description
      timestamp
    }
  }
}

query {
  # Fetch fields that often reflect database content and may show SQL errors or injected payloads
  getWallets {
    getWallets
    name
    balance
    # transaction descriptions and user descriptions are common places to detect SQL error strings or un-sanitized DB output
    transactions {
      id
      description
    }
    user {
      id
      description
      firstName
      lastName
    }
    # alias to mark probe in response keys
    sql_probe_OR_1_eq_1: id
  }
}

query {
  # Retrieve fields which may contain stored HTML/JS from users
  getWallets {
    getWallets
    name
    user {
      id
      firstName
      description
    }
    transactions {
      id
      description
    }
    # alias to indicate XSS probe
    xss_probe_description: transactions {
      id
      description
    }
  }
}

query {
  # Retrieve fields that may contain URLs or remote resource identifiers (location.name, currency.country sometimes contain external refs)
  getWallets {
    getWallets
    name
    transactions {
      id
      description
      location {
        id
        name
        lat
        lng
      }
    }
    currency {
      id
      abbreviation
      country
    }
    ssrf_probe_location_name: transactions {
      location {
        name
      }
    }
  }
}

query {
  # Fetch fields that might reflect outputs of backend commands if the system is vulnerable
  getWallets {
    getWallets
    name
    user {
      id
      firstName
      description
    }
    transactions {
      id
      description
    }
    os_cmd_probe: user {
      description
    }
  }
}

query {
  # Request fields that may include file paths or be influenced by path traversal inputs
  getWallets {
    getWallets
    name
    balance
    user {
      id
      description
    }
    transactions {
      id
      description
      location {
        id
        name
      }
    }
    # probe alias label referencing typical path traversal pattern for analyst correlation
    path_traversal_probe_etc_passwd: id
  }
}

query {
  # Retrieve text fields that may contain un-escaped HTML
  getWallets {
    getWallets
    name
    user {
      id
      description
      firstName
    }
    transactions {
      id
      description
    }
    html_probe_transaction_description: transactions {
      description
    }
  }
}

query {
  # Non-aliased normal call (intended to test direct access control)
  getWallets {
    getWallets
    name
    balance
    user {
      id
      firstName
    }
  }
}

query {
  # Aliased call to test whether access control can be bypassed by aliasing the root field
  s: getWallets {
    getWallets
    name
    balance
    user {
      id
      firstName
    }
  }
}

query {
  # Include getWallets as requested while also performing introspection to see if introspection is exposed
  getWallets {
    getWallets
  }
  __schema {
    queryType { name }
    types {
      name
      kind
      fields {
        name
        type {
          name
          kind
        }
      }
    }
  }
}

query {
  # Request intentionally misspelled fields to elicit GraphQL validation suggestions (e.g., "fristName" -> "firstName", "balnce" -> "balance")
  getWallets {
    getWallets
    name
    balnce
    user {
      id
      fristName
      lastName
    }
  }
}
