query.query:
mutation updateTransaction {
  updateTransaction(input: {
    transactionID: "tx-0001",
    amount: 100.5,
    payer: "user-100",
    description: "Payment for invoice 2025-09"
  }) {
    id
    amount
    rate
    payer { id firstName lastName }
    wallet { id name balance }
    description
    timestamp
  }
}

