    transactionID: "tx-0002'; DROP TABLE transactions; --",
    amount: 0.0,
    payer: "user-101",
    description: "Attempt SQL injection: '); DROP TABLE transactions; --"
  }) {
    id
    amount
    description
    payer { id firstName }
