query.query:
mutation {
  updateTransaction(input: {
    transactionID: "txn-1001",
    amount: 49.95,
    payer: "user-42",
    description: "Payment for invoice #INV-2025-09"
  }) {
    id
    amount
    rate
    description
    timestamp
    payer {
      id
      firstName
      lastName
    }
    wallet {
      id
      name
      balance
      currency {
        abbreviation
        symbol
      }
    }
    location {
      id
      name
      lat
      lng
    }
  }
}

