query {
  updateTransaction(name: "Transaction") {
    name
    fields {
      name
      type {
        name
        kind
        ofType { name kind }
      }
      args { name type { name kind ofType { name } } }
    }
  }
}

query {
  updateTransaction(name: "Wallet") {
    name
    fields {
      name
      type { name kind }
    }
  }
}
