mutation {
  updateTransaction(input: {
    transactionID: "txn_000123",
    amount: 125.50,
    payer: "user_42",
    description: "Payment for invoice #INV-2025-09"
  }) {
    id
    amount
    description
    timestamp
    payer {
      id
      firstName
      lastName
