mutation updateWallet {
  updateWallet(input: { walletID: "3fa85f64-5717-4562-b3fc-2c963f66afa6", name: "Primary Savings" }) {
    id
    name
    balance
    currency {
      id
      abbreviation
      symbol
    }
    user {
