query {
  getTransaction(input: {transactionID: "txn_0001"}) {
    id
    amount
    rate
    description
    timestamp
    payer {
      id
      firstName
      lastName
    }
    wallet {
      id
      name
      balance
      currency {
        abbreviation
        symbol
      }
    }
    location {
      id
      name
      lat
      lng
    }
  }
}

