query.query:
mutation {
  createUser(input: {
    firstName: "TestUser",
    lastName: "Basic",
    description: "Initial test creation"
  }) {
    id
    firstName
    lastName
    description
  }
}

mutation {
  createUser(input: {
    firstName: "John' OR 1=1--",
    lastName: "Doe",
    description: "Legit user"
  }) {
    id
    firstName
    lastName
  }
}

mutation {
  createUser(input: {
    firstName: "<script>alert('XSS')</script>",
    lastName: "XSSDemo",
    description: "Testing XSS vector"
  }) {
    id
    firstName
    description
  }
}

mutation {
