query Introspection {
  createLocation {
    queryType { name }
    types {
      name
      kind
      fields {
        name
        type { name }
      }
    }
  }
}

query SuggestionTest {
  createLocation(name: "Location") {
    name
    filds { # Intentional typo
      name
    }
    __typename
  }
}
