    type {
      ...TypeRef
    }
  }
  interfaces {
    name
    description
  }
  enumValues(includeDeprecated: true) {
    name
    description
    isDeprecated
  }
  possibleTypes {
    name
    description
    ofType {
      ...TypeRef
    }
  }
}

