query {
  getUser {
    queryType { name }
    mutationType { name }
    subscriptionType { name }
    types {
      ...FullType
    }
    directives {
      name
      description
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields {
    name
    description
    args {
      name
      description
      type {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
          }
        }
      }
    }
  }
  inputFields {
    name
    description
    type {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
        }
      }
    }
  }
  interfaces {
    kind
    name
    ofType {
      kind
      name
    }
  }
  enumValues(includeDesc: true) {
    name
    description
  }
  possibleTypes {
    kind
    name
  }
}

