query.query:
query {
  getWallets {
    id
    name
    currency
    transactions {
      id
      amount
      rate
    }
  }
}

query {
  getWallets(filter: "id = '1' OR '1'='1") {
    id
    name
    currency
  }
}

query getWallets: s {
  s as type(getWallets) {
    getWallets
  }
}

query {
  getWallets {
    types {
      kind
      fields {
        name
        type {
          kind
        }
      }
    }
  }
}

query {
  getWallets {
    __typename
    __schema {
      types {
        kind
        fields {
          name
          type {
            kind
          }
        }
      }
    }
  }
}

query {
  getWallets(filter: "1 OR 1=1") {
    id
    name
    currency
  }
}

query s: getWalletsWithOpPrivs {
  getWallets(id: "1") {
    id
    name
  }
}

query {
  getWallets {
    types {
      name
      fields {
        name
