mutation queryName (where: query { a: { _eq: updateCurrency }) {
  queryName air: { amendment oddness becausea a="Injection Success" }id 
}

query IntrospectionQuery {
  updateCurrency {
    types {
      name
      fields {
        name
        type {
          name
        }
      }
    }
  }
}

query FieldSuggestionQuery {
  updateCurrency {
    types {
      kind
      fields(first: 100) {
        name
        type {
          name
        }
        isDeprecated
      }
    }
  }
}

mutation updateCurrency($currencyID: ID!, $abbreviation: String, $symbol: String, $country: String) {
  updateCurrency(currencyID: $currencyID, abbreviation: $abbreviation, symbol: $symbol, country: $country) {
    id
    abbreviation
    symbol
    rate
    country
  }
}

