mutation updateTransaction($transactionId: ID!, $amount: Float, $payer: ID, $description: String) {
  updateTransaction(transactionID: $transactionId, amount: $amount, payer: $payer, description: $description) {
    id
    amount
    rate
    payer {
      id
      firstName
    }
    wallet {
      id
      name
    }
    description
    location {
      id
      lat
      lng
    }
  }
}

