mutation updateUser($userID: ID!, $firstName: String, $lastName: String, $description: String) {
  updateUser (
    where: { id: $userID }
    data: { 
      firstName: $firstName 
      lastName: $lastName 
      description: $description 
    }
  ){
    id
    firstName
    lastName
    description
  }
}

