query.query:
schema {
  deleteUser deleteUser(input: { userId: "ID!" }) { type: SUCCESS }
  type User {
    id: ID!
    firstName: String!
    lastName: String
    description: String
    wallets: [Wallet]
    friends: [User]
  }
  type Wallet {
    id: ID!
    name: String!
    currency: Currency!
    transactions: [Transaction]
    user: User!
    balance: Float
  }
  type Currency {
    id: ID!
    abbreviation: String!
    symbol: String!
    rate: Float!
    country: String!
  }
  type Transaction {
    id: ID!
    amount: Float!
    rate: Float!
    payer: User!
    wallet: Wallet!
    description: String
    location: Location
    timestamp: String
  }
  type Location {
    id: ID!
    lat: Float
    lng: Float
    name: String!
  }
}

mutation deleteUser {
  deleteUser(input: { userId: "123' OR 1=1" }) {
    success
  }
}

mutation deleteUser {
  q: deleteUser(input: { id: 123 }) {
    s
  }
}
