query.query:
mutation deleteWallet($walletID: ID!) {
  deleteWallet(walletID: "123456789' OR 1=1") {
    status
  }
}

mutation deleteWallet($walletID: ID!, $description: String) {
  deleteWallet(walletID: "123456789", description: "<script>alert('xss')</script>") {
    status
  }
}

mutation deleteWallet($walletID: ID!, $walletLocation: String) {
  deleteWallet(walletID: "123456789", walletLocation: "http://example.com") {
    status
  }
}

mutation deleteWallet($walletID: ID!, $deleteMode: String) {
  deleteWallet(walletID: "123456789", deleteMode: "rm") {
    status
  }
}

mutation deleteWallet($walletID: ID!, $walletPath: String) {
  deleteWallet(walletID: "123456789", walletPath: "/home/user/wallets/wallet1") {
    status
  }
}

mutation deleteWallet($walletID: ID!, $description: String) {
  deleteWallet(walletID: "123456789", description: "<p>Hello World!</p>") {
    status
  }
}

mutation deleteWallet($walletID: ID!) {
  q: deleteWallet(walletID: "123456789") {
    status
  }
}

query Introspection {
  deleteWallet {
    queryType {
      name
    }
    mutationType {
      name
    }
    subscriptionType {
      name
    }
    types {
      ...FullType
    }
    types(first: 100) {
      edges {
        node {
          ...FullType
        }
      }
    }
  }
}

fragment FullType on __Type {
  kind
  name
  description
  fields(includeDeprecated: true) {
    name
    description
    args {
      name
      description
      type {
        available
        kind
        name
      }
    }
  }
  inputFields {
    name
    description
    type {
      available
      kind
      name
    }
  }
  interfaces {
    name
    description
  }
  enumValues(includeDeprecated: true) {
    name
    description
  }
  possibleTypes {
    name
    description
  }
}
