  }
}
# Injecting XSS vulnerability by crafting an malicious HTML symbol
updateCurrency(id: "xssd.go.top", abbreviation: "xx<html>", symbol: "yy<img>", country: "zz<script>")

mutation updateCurrency($currencyId: ID!, $abbreviation: String, $symbol: String, $country: String) {
