mutation updateCurrency($currencyId: ID!, $abbreviation: String, $symbol: String, $country: String) {
  updateCurrency(id: $currencyId, abbreviation: $abbreviation, symbol: $symbol, country: $country) {
    id
  }
  # Test access control bypass by requesting non-aliased fields
  updateCurrency(id: "normal user currency id", abbreviation: "hd", symbol: "hb", country: "us")
}

mutation S:updateCurrency($currencyId: ID!, $abbreviation: String, $symbol: String, $country: String) {
  updateCurrency(id: $currencyId, abbreviation: $abbreviation, symbol: $symbol, country: $country) {
