  updateCurrency(id: "aliased query id", abbreviation: "hd", symbol: "hb", country: "us")
}

query Introspect {
  updateCurrency {
    types {
      kind
      name
      fields {
        name
        type {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
              }
            }
          }
        }
      }
    }
  }
}

query SuggestFieldNames {
  updateCurrency(name: "Currency") {
    fields {
      name
      type {
        kind
      }
    }
  }
}
