mutation updateCurrency($currencyID: ID!, $abbreviation: String, $symbol: String, $country: String) {
  updateCurrency(currencyID: "USD" OR id = "USD", abbreviation: $abbreviation, symbol: $symbol, country: $country) {
    id
    abbreviation
    symbol
    rate
    country
  }
}

