query.query:
mutation updateLocation($locationID: ID!, $lat: Float, $lng: Float, $name: String!) {
  updateLocation(locationID: $locationID, lat: $lat, lng: $lng, name: $name) {
    id
    lat
    lng
    name
  }
}

mutation updateLocation(locationID: "123 OR 1=1", lat: 0.0, lng: 0.0, name: "") {
  updateLocation(locationID: "123 OR 1=1", lat: 0.0, lng: 0.0, name: "")
}

mutation updateLocation(locationID: "123", lat: 0.0, lng: 0.0, name: "<script>alert('xss')</script>") {
  updateLocation(locationID: "123", lat: 0.0, lng: 0.0, name: "<script>alert('xss')</script>")
}

mutation updateLocation(locationID: "http://example.com", lat: 0.0, lng: 0.0, name: "") {
  updateLocation(locationID: "http://example.com", lat: 0.0, lng: 0.0, name: "")
}

mutation updateLocation(locationID: "bash", lat: 0.0, lng: 0.0, name: "") {
  updateLocation(locationID: "bash", lat: 0.0, lng: 0.0, name: "")
}

mutation updateLocation(locationID: "/etc/passwd", lat: 0.0, lng: 0.0, name: "") {
  updateLocation(locationID: "/etc/passwd", lat: 0.0, lng: 0.0, name: "")
}

mutation updateLocation(locationID: "123", lat: 0.0, lng: 0.0, name: "&html injection&&"") {
  updateLocation(locationID: "123", lat: 0.0, lng: 0.0, name: "&html injection&&")
}

mutation updateLocation(locationID: "HACKED BY:INFO", lat: 0.0, lng: 0.0, name: "") {
  updateLocation(locationID: "HACKED BY:INFO", lat: 0.0, lng: 0.0, name: "")
}

mutation qrb:a@updateLocation(l: "HACKED BY:INFO", O: 0.0, b: 0.0, i: "") {
  updateLocation:a@updateLocation(l: "HACKED BY:INFO", O: 0.0, b: 0.0, i: "")
}

mutation Introspection {
  updateLocation {
    types {
      name
      fields(includeDeprecated: true) {
        name
        type {
          kind
          ofType {
            kind
          }
        }
      }
    }
  }
}

mutation Field_Suggestions {
  updateLocation(locationID: "123", lat: 0.0, lng: 0.0, name: "too long name that exceeds max length") {
    id
    lat
    lng
    name
  }
}
