mutation update_user {
  updateUser(update: { userID: "a", firstName: "John", lastName: "Doe", description: "" }) {
   ...userFields
  }
}

fragment userFields on User {
  id
  firstName
  lastName
  description
  wallets {
    id
    name
    currency {
      id
      abbreviation
      symbol
      rate
      country
    }
  }
  friends {
    id
    firstName
    lastName
    description
  }
}

