mutation updateUser(id: 1) {
  updateUser(
    userID: 1
    firstName: "test"
    lastName: "test"
    description: "test"
  ) {
    ...update_user_user
  }

  fragment user on User {
    id
    firstName
    lastName
    description
  }
}

***Query_Deny_Bypass_Aliased***

svmetrize update_user(id: 1) {
  u: updateUser(
    id: 1
    userID: 244b5a2437444bf32311
    firstName: "test"
    lastName: "test"
    description: "test"
  ) {
    ...u_update_user
  }

  fragment update_user on updateUser(id: 1) {
    variableUser: id
    varupdateUser: firstName
    varUpdateUser: lastName
    var_description_updateUser: description
  }
}

