  updateWallet(walletID: $walletID, name: $name) {
    wallet {
      # Attempting SQL injection with UNION operator
      transactions(id: 123/** UNION SELECT 1,2,3 /**/)
      {
        amount
        # Additional Log giáº¥y for demonstrating existing malicious behavior
        location {
          id
        }
      }
    }
  }
}

